`,(0,a.jsxDEV)(r.pre,{children:(0,a.jsxDEV)(r.code,{className:"language-tsx",children:`export function useForm<T>(initialValues: T): [T, FormEventHandler<HTMLFormElement>] {
    const [data, setData] = useState<T>(initialValues);
    const onSubmit: React.FormEventHandler<HTMLFormElement> = (e) => {
        e.preventDefault();

        const inputs = [...e.currentTarget];
        const obj: T = inputs.reduce((acc: T, crr: Element) => {
            const { name, value } = crr as HTMLInputElement;
            return { ...acc, [name]: value };
        }, {} as T);

        setData(obj);
    };

    return [data, onSubmit];
}
