`,(0,a.jsxDEV)(r.pre,{children:(0,a.jsxDEV)(r.code,{className:"language-tsx",children:`function App() {
    const [data, onSubmit] = useForm<Info>({ name: "", age: "", location: "" });
    return (
        <>
            <form
                className="form"
                onSubmit={onSubmit}
            >
                <input
                    name="location"
                    type="text"
                    placeholder="location"
                />

                <input
                    name="name"
                    type="text"
                    placeholder="name"
                />
                <input
                    name="age"
                    type="number"
                    placeholder="age"
                />
                <input type="submit" />
            </form>

            <div>
                <p>Name : {data.name}</p>
                <p>age : {data.age}</p>
                <p>location : {data.location}</p>
            </div>
        </>
    );
}

export default App;
